mapper.update:
update t_unit
	set
	lastXunchaTime = sysdate()
	 ,isgm= #{isgm} 
	 ,isxc= #{isxc} 
	 ,starLevel= #{starLevel} 
	 ,iscg= #{iscg} 
where id=#{id}

mapper.select:
select unit.id as "id", unit.name as "name", unit.departmentId as "departmentId", unit.townId as "townId", unit.code as "code", unit.license as "license", unit.address as "address", unit.type as "type", unit.safedLinkman as "safedLinkman", unit.safedTelphone as "safedTelphone", unit.manageLinkman as "manageLinkman", unit.manageTelphone as "manageTelphone", unit.belongUserId as "belongUserId", unit.isxc as "isxc", unit.addTime as "addTime", unit.modTime as "modTime", unit.userId as "userId", unit.delflag as "delflag", unit.meno as "meno", u.name as "userName", t.name as "townName", dep.name as "departmentName", unit.bimg as "bimg", unit.area as "area", unit.buildingsLayer as "buildingsLayer", unit.iscg as "iscg", unit.isgm as "isgm", unit.lastXunchaTime as "lastXunchaTime", unit.keyUnit as "keyUnit" from t_unit unit left join t_user u on u.id=unit.userId left join t_town t on t.id=unit.townId left join t_department dep on dep.id=unit.departmentId
1=1 and unit.iscg != '1' and unit.townId=#{params.townId} and unit.userId=#{params.userId} and (unit.name like #{params.unitName} or unit.address like #{params.address})
order by unit.addTime desc limit #{first},#{pageSize}
