mapper.update:
update t_xunchard set
	imgIds= #{imgIds}
where xunchaId=#{id}

mapper.select:
select id as "id", xctype as "xctype", unitId as "unitId", unitName as "unitName", departId as "departId", departName as "departName", townId as "townId", townName as "townName", xcTime as "xcTime", oxcTime as "oxcTime", xcPerson as "xcPerson", etPerson as "etPerson", xcItem1 as "xcItem1", xcItem2 as "xcItem2", xcItem3 as "xcItem3", xcItem4 as "xcItem4", xcItem5 as "xcItem5", xcItem6 as "xcItem6", xcItem7 as "xcItem7", xcItem8 as "xcItem8", xcItem9 as "xcItem9", xcItem10 as "xcItem10", xcItem11 as "xcItem11", meno as "meno", flag as "flag", addTime as "addTime", modTime as "modTime", userId as "userId", addIp as "addIp", pxquantity as "pxquantity", trainingA as "trainingA", app_xuncha_id as "appXunchaId", doorNum as "doorNum", doorTime1 as "doorTime1", doorTime2 as "doorTime2", live_three as "liveThree" from t_xuncha
unitId=#{unitId}
order by addTime desc

select x.id as "id", x.unitId as "unitId", x.unitName as "unitName", x.xcTime as "xcTime" from t_xuncha x
x.flag='11' and x.townId=#{params.townId} and x.unitName like #{params.unitName}
order by x.xcTime desc limit #{first},#{pageSize}
